import ReactDOM from 'react-dom';
import React, { Component } from 'react';
import logo from '../../layout/img/NetMx-nav.svg';
import netmx from '../../layout/img/NetMX.svg';
import curva from '../../layout/img/curva.png';
import Constantes from "../../Constantes";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
class Navbar extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            traducciones: [],
        };
    }
    async componentDidMount() {
        fetch(`${Constantes.RUTA_API}/obtener_traduccion.php`) 
  .then(result=>result.json())
  .then(traducciones=>this.setState({traducciones}))
        const respuesta = await fetch(`${Constantes.RUTA_API}/obtener_traduccion.php`);
        const traducciones = await respuesta.json();
        this.setState({
            traducciones: traducciones,
        });

        console.log(this.state.traducciones)
    }
    render() {
        const {items} = this.state
      return (
          <>

          <header>

        <nav className="navbar navbar-expand-lg navbar-dark menu menu--ferdinand"  id="nav-main">
        <a className="navbar-brand" href="index.php">
            <img src={logo} height="50" alt="NetMX" />
        </a>

        <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNavDropdown"
                aria-controls="navbarNavDropdown" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNavDropdown">
            <ul className="navbar-nav ml-auto menu__list">
                <li className="nav-item active menu__item menu__item--current">
                    <a className="nav-link menu__link" href="#inicio" data-scroll >Inicio <span
                            className="sr-only">(current)</span></a>;

                            </li>

                <li className="nav-item menu__item">
                    <a className="nav-link menu__link" data-scroll href="#nosotros" >Nosotros </a>
                </li>
                <li className="nav-item menu__item">
                    <a className="nav-link menu__link" data-scroll href="#servicios">Servicios</a>
                </li>
                <li className="nav-item menu__item">
                    <a className="nav-link menu__link" data-scroll href="#productos">Productos</a>
                </li>
                <li className="nav-item menu__item">
                    <a className="nav-link menu__link" data-scroll href="#portafolio">Portafolio</a>
                </li>
                <li className="nav-item menu__item">
                    <a className="nav-link menu__link" data-scroll href="#contacto">Contacto</a>
                </li>

            </ul>
        </div>
        </nav>
        <div className="hero" id="inicio">
        <div className="curva-hero"  >
        </div>
        <div className="content-hero">
        <div className="row align-items-center wrap-section">
            <div className="col-10 col-md-5 col-lg-6 col-xl-5">
                <div className="text-hero aos-init aos animate" data-aos="fade-up" data-aos-duration="1000">
                    <h1>
                        <b>Más de 25 AÑOS</b><br/>
                        <span>de experiencia en el manejo</span> <br/>
                        <b>de información web</b>
                        </h1>
                    <div className="pleca-hero"></div>
                    <p className="descripcion-hero">Somos expertos en proveer soluciones seguras de manejo de información
                        basadas en web para un ambiente corporativo.</p>

                </div>
            </div>
            <div className="col-12 col-md-6 col-lg-6 col-xl-6 d-none d-md-block" data-aos="fade-up"
                 data-aos-duration="1000" data-aos-delay="800">
                <div className="text-hero text-right">
                <img src={netmx} height="350" alt="NetMX" width="350" />

                </div>
            </div>
        </div>
    </div>

    <div className="cuadros-hero aos-init aos animate" data-aos="fade-down" data-aos-duration="1000" data-aos-delay="400" style={{ backgroundImage:{curva} }}> 
    </div>
        </div>
  </header>
  )
})
}
</>
)
}
